mapper.select:
SELECT 

FROM bm_cont_item a

WHERE a.id = #{id}

SELECT 

FROM bm_cont_item a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY create_date

SELECT 

FROM bm_cont_item a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE bm_cont_item SET 	
	content = #{content},
	contract_id = #{contractId}
WHERE id = #{id}

UPDATE bm_cont_item SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
