mapper.select:
SELECT 

FROM bm_contract a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.value = #{value}


		AND a.type = #{type}


		and a.id not in

			#{item}




			and	a.province_id = #{province.id}




			and	a.city_id = #{city.id}



		and	DATE_FORMAT(a.signed_time,"%Y") = #{year}


		and	 class=#{clazz}


		and	value>=#{min}


		and	#{max}>=value


		and	#{max}>=value



			and    p.office_id = #{office.id}



	${sqlMap.dsf}



		ORDER BY ${page.orderBy}

SELECT 

FROM bm_contract a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE bm_contract SET
 del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
