mapper.select:
SELECT  distinct

FROM bm_contract a

WHERE 1=1

 and	a.id = #{id}


	and	a.code = #{code}

SELECT distinct

FROM bm_contract a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE concat('%',#{name},'%')


		AND a.value = #{value}


		AND a.type = #{type}


		and a.id not in

			#{item}




			and	a.province_id = #{province.id}




			and	a.city_id = #{city.id}



		and	DATE_FORMAT(a.signed_time,"%Y") = #{year}


		and	 class=#{clazz}


		and	value>=#{min}*10000


		and	#{max}*10000>=value



			and    p.office_id = #{office.id}




		 and t.type_id in

			               #{item}



	and	a.first_Party like CONCAT('%',#{firstParty},'%')



	${sqlMap.dsf}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date desc

SELECT distinct

FROM bm_contract a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE bm_contract SET
 del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
