mapper.select:
SELECT 

FROM bm_contract a

WHERE 1=1

 and	a.id = #{id}


 and    a.code = #{code}

SELECT 

FROM bm_contract a




		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.value = #{value}


		AND a.type = #{type}


		and a.id not in

			#{item}





		ORDER BY ${page.orderBy}

SELECT 

FROM bm_contract a






		ORDER BY ${page.orderBy}
