SELECT 

FROM bm_distribute a






		ORDER BY ${page.orderBy}

select distinct c.group_id from bm_distribute a inner join bm_rule_item b on a.rule_item_id=b.id inner join bm_rule c on b.rule_id=c.id inner join bm_rule_office d on d.rule_id=c.id where 1=1
and a.income_id=#{incomeId}
and d.office_id=#{officeId}

SELECT account as "account", sum(value) as "value", income_id as "incomeId" from bm_distribute
income_id=#{incomeId}
group by account

mapper.update:
		UPDATE bm_distribute
		SET status = #{status}
        WHERE income_id= #{incomeId}
