mapper.select:
SELECT 

FROM bm_rule_group a

WHERE a.id = #{id}

SELECT
FROM bm_rule_group a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}

SELECT

FROM bm_rule_group a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE bm_rule_group SET 	
	name = #{name},
	remarks = #{remarks}
WHERE id = #{id}

UPDATE bm_rule_group SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
