mapper.select:
SELECT 

FROM bm_rule_item a

WHERE a.id = #{id}

SELECT
FROM bm_rule_item a
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.rule_id = #{ruleId}
ORDER BY ${page.orderBy}

mapper.update:
UPDATE bm_rule_item SET 	
	name = #{name},
	percent = #{percent},
	rule_id = #{ruleId},
	remarks = #{remarks}
WHERE id = #{id}
