mapper.select:
SELECT * FROM sys_dict WHERE del_flag = #{DEL_FLAG_NORMAL}
AND type = #{type}
AND description LIKE '%'||#{description}||'%' '%'+#{description}+'%' CONCAT('%', #{description}, '%')
ORDER BY type, sort, update_date DESC

mapper.update:
UPDATE sys_dict SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT a.id AS "id", a.parent_id AS "parent.id", a.parent_ids AS "parentIds", a.name AS "name", a.sort AS "sort", a.create_by AS "createBy.id", a.create_date AS "createDate", a.update_by AS "updateBy.id", a.update_date AS "updateDate", a.remarks AS "remarks", a.del_flag AS "delFlag" FROM sys_dict_professional a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.parent_id = #{parent.id}
ORDER BY a.sort ASC
